// node-sass编译sass
const path = require("path");
const fs = require("fs");
const sass = require('sass');
const fsEx = require("fs-extra");

function renderCss (config) {
    sass.render({
        file: config.scss
    }, (err, result) => {
        fsEx.ensureFileSync(config.css)
        fs.writeFileSync(path.resolve(config.css), result.css);
    });
}

const build = (config) => {
    config.forEach((val, index) => {
        renderCss(val)
    })
}
module.exports = build

